mapper.select:

SELECT COUNT(id)
FROM sys_user
WHERE username = #{username}

SELECT su.id, su.username, su.nickname, su.avatar, su.phone, su.email, su.status, sr.id AS roleId, sr.role_name, su.create_time, su.update_time FROM sys_user su LEFT JOIN sys_user_role sur ON su.id = sur.user_id LEFT JOIN sys_role sr on sr.id = sur.role_id
AND su.username LIKE CONCAT ('%',#{param.username},'%')
AND su.`status` = #{param.status}
AND su.create_time BETWEEN #{param.startTime} AND #{param.endTime}
